mapper.select:
        select * from `electric-meter` em
		left join company c on em.companyId = c.id        
         
             and em.companyId = ${companyId}
         
         limit ${pageSize} offset ${pageNo*pageSize-pageSize};

select count(*) from `electric-meter` em
where status = 0
 
     and em.companyId = ${companyId}
 
 limit ${pageSize} offset ${pageNo*pageSize-pageSize};
